package org.quipa.dialogo

class App {

    static void main(String[] args) {
        println "Bem-vindo ao 'Dialogo'!"
        // println "SPAM"
        def interpretador = new Interpretador()
        def console = System.console()
        String frase = ""
        while (frase != "sair") {
            // this doesn't work when running through gradle run
            // use gradle installDist and ./app/build/install/app/bin/app
            frase = console.readLine(' - ')
            
            switch (frase) {
                case "hist贸ria":
                    println(interpretador.hist贸ria)
                    break
                default:
                    println("   " + frase)
                    interpretador.hist贸ria += "${frase}\n"
            }
            
        }
    }
}

class Interpretador {
    String hist贸ria = ""
    
    List<String> ler(String texto) {
        return texto.tokenize()
    }
    
    List<String> avaliar(List<String> items) {
        return items
    }
    
    String imprimir(List<String> items) {
        return items.join()
    }
}
